Dialog.ClientEvent:
var dom = $(arg.dom).find(".d-caption-bar.d-dialog-caption-bar")[0];
var userSearch = new dorado.widget.TextEditor({
	exClassName: "user-search-txt"
});
userSearch.render(dom);
$(dom).find("input").on("keydown", function(e) {
	var inputDom = this;
	if (e.keyCode === 13) {
		return;
	}
	window.clearTimeout(self.userQueryTask);
	self.userQueryTask = window.setTimeout(function(){
		var key = $(inputDom).val();
		if (key) {
			activeGroupList.set("visible", false);
			userResult.set("visible", true);
			dsUser.set("parameter", key).flushAsync();
		}
		else {
			activeGroupList.set("visible", true);
			userResult.set("visible", false);
		}
	}, 150);
});

self.noticeCount = 0;
self.onReceiveListeners = [];
self.addReceiveListener = function(l) {
	self.onReceiveListeners.push(l);
};
self.onReceive = function(socket, arg) {
	self.doReceiveNotice(socket, arg);
	self.onReceiveListeners.each(function(l) {
		l(self, {socket: socket, param: arg});
	});
};

self.connectNotifyServer = function() {
	self._noticeSocket = dorado.Socket.connect({
		service: "noticeController#connectServer",
		parameter: "${loginUsername}",
		onReceive: function(arg) {
			self.onReceive(view._notifySocket, arg)
		},
		onDisconnect: function() {

		}
	});
};

self.send = function(notice) {
	self._noticeSocket.send(notice.type, notice);
}

self.connectNotifyServer();

$(window).bind("beforeunload", function(){
	self._noticeSocket.disconnect();
	return;
});

self.doReceiveNotice = function(s, arg) {
	if(arg.type === "message") {
		var groups = dsActiveGroup.getData();
		var group = null;
		groups.each(function(g) {
			if (g.get("id") === arg.data.groupId) {
				group = g;
				return false;
			}
		});

		var callback = function() {
			if (!group.noticeCount) {
				group.noticeCount = 0;
			}
			arg.data.$dataType = "Notice";
			group.set("lastNotice", arg.data);
			delete arg.data.$dataType;
			group.createChild("notices", arg.data);

			if (groups.getFirst() !== group) {
				groups.insert(group, "begin");
				group.setState(dorado.Entity.STATE_NONE);
			}
			if (arg.data.sender === "${loginUsername}") {
				return;
			} else {
				self.noticeCount++;
				group.noticeCount++;
			}
			if (groups.refreshCountTask) {
				clearTimeout(group.refreshCountTask);
			}
			group.refreshCountTask = setTimeout(function() {
				var count = group.noticeCount > 99 ? "99+" : group.noticeCount;
				$("#group-count-" + group.entityId).css("display", group.noticeCount ? "inline-block" : "none").text(count);
				if (group.noticeCount) {
					var count = group.noticeCount > 99 ? "99+" : group.noticeCount;
					$("#group-count-" + group.entityId).css("display", group.noticeCount ? "inline-block" : "none").text(count);
					if (group.noticeCount > 9) {
						$("#group-count-" + group.entityId).css("padding", "2px 4px");
					} else {
						$("#group-count-" + group.entityId).css("padding", "2px");
					}

				}
			}, 80);
		};
		if (group) {
			callback();
		} else {
			dsAG.set("parameter", {
				groupId: arg.data.groupId,
				memberId: "${loginUsername}"
			}).flushAsync(function(data) {
				group = groups.insert(data.toJSON(), "begin");
				callback();
			});
		}

	} else if (arg.type === "mark-read") {
		var groups = dsActiveGroup.getData();
		groups.each(function(g) {
			if (g.get("id") === arg.data.groupId) {
				self.noticeCount -= g.noticeCount;
				g.noticeCount = 0;
				$("#group-count-" + g.entityId).css("display", "none");
				return false;
			}
		});
	}
};

var group = dsActiveGroup.getData("#");
if (group && group.noticeCount > 0) {
	Chat.send({
		type: "mark-read",
		sender: "${loginUsername}",
		groupId: group.get("id")
	})

}

DataListBox.ClientEvent:
$(arg.dom).empty();
var my = arg.data.get("sender") === "${loginUsername}";
var name = "";
var tip = new dorado.widget.Tip({
	content: arg.data.get("content").replace(/\n/g, "<br>"),
	floating: false,
	arrowDirection: my ? "right" : "left",
	arrowAlign: "top"
});
var avatar = new dorado.widget.Control({
	exClassName: "message-avatar " + (my ? "right" : "left")
});
if (my) {
	name = "${loginUser.nickname}"[0].toLocaleUpperCase();
} else {
	name = dsActiveGroup.getData("#.other.nickname")[0].toLocaleUpperCase();
}
$(avatar.getDom()).text(name);

avatar.render(arg.dom);
tip.render(arg.dom);

TextArea.ClientEvent:
var content = self.get("value");
if (arg.keyCode === 13 && content && !arg.shiftKey) {
	var group = dsActiveGroup.getData("#");
	if (!group) {
		return;
	} 
	Chat.send({
		type: "message",
		sender: "${loginUsername}",
		groupId: group.get("id"),
		all: group.get("all"),
		content: content
	});
	self.set("value", null);
	arg.returnValue = false;
}

var group = dsActiveGroup.getData("#");
if (group && group.noticeCount > 0) {
	Chat.send({
		type: "mark-read",
		sender: "${loginUsername}",
		groupId: group.get("id")
	})

}

DataListBox.ClientEvent:
$(arg.dom).empty();
var privateLetter = arg.data.get("privateLetter");
var n = "";
var d = "";
if (privateLetter) {
	n = arg.data.get("other").get("nickname");
} else {
	n = arg.data.get("name") || "æ— ";
}
if (arg.data.get("lastNotice")) {
	d = arg.data.get("lastNotice").get("content");
}

var avatar = new dorado.widget.Control({
	exClassName: "group-avatar"
});
var name = new dorado.widget.Control({
	exClassName: "group-name",
	tip: n
});
var description = new dorado.widget.Control({
	exClassName: "group-description",
	tip: d
});

var count = new dorado.widget.Control({
	exClassName: "group-count"
});

$(avatar.getDom()).text(n[0].toLocaleUpperCase());
$(name.getDom()).text(n);
$(description.getDom()).text(d);


avatar.render(arg.dom);
name.render(arg.dom);
count.render(arg.dom);
description.render(arg.dom);
$(arg.dom).find(".group-count").attr("id", "group-count-" + arg.data.entityId)

var receiver = dsUser.getData("#");
var groups = dsActiveGroup.getData();
var group = null;
groups.each(function(g) {
	if (g.get("other.memberId") === receiver.get("username")) {
		group = g;
		return false;
	}	
});
if (group) {
	if (groups.getFirst() !== group) {
		groups.insert(group, "begin");
		group.setState(dorado.Entity.STATE_NONE);
	}
} else {
	dsPLG.set("parameter", {
		memberId: "${loginUsername}",
		otherId: receiver.get("username")
	}).flushAsync(function(data) {
		if (data) {
			group = groups.insert(data.toJSON(), "begin");
			group.setState(dorado.Entity.STATE_NONE);
		} else {
			var group = groups.insert({
				privateLetter: true,
				memberCount: 2,
				creator: "${loginUsername}",
				other: {
					memberId: receiver.get("username"),
					nickname: receiver.get("nickname"),
					active: true
				}
			}, "begin");
			group.createChild("members", {
				memberId: "${loginUsername}",
				nickname: "${loginUser.nickname}",
				active: true
			});
			group.createChild("members", {
				memberId: receiver.get("username"),
				nickname: receiver.get("nickname"),
				active: true
			});
			updateActionActiveGroup.execute();

		}
	});
}

activeGroupList.set("visible", true);
userResult.set("visible", false);

$(arg.dom).empty();
var n = arg.data.get("nickname");
var d = arg.data.get("username");

var avatar = new dorado.widget.Control({
	exClassName: "group-avatar"
});
var name = new dorado.widget.Control({
	exClassName: "group-name",
	tip: n
});
var description = new dorado.widget.Control({
	exClassName: "group-description",
	tip: d
});

$(avatar.getDom()).text(n[0].toLocaleUpperCase());
$(name.getDom()).text(n);
$(description.getDom()).text(d);


avatar.render(arg.dom);
name.render(arg.dom);
description.render(arg.dom);
