Dialog.ClientEvent:
self.noticeCount = 0;
self.onReceiveListeners = [];
self.onChatCloseListeners = [];
self.addReceiveListener = function(l) {
	self.onReceiveListeners.push(l);
};
self.addChatCloseListener = function(l) {
	self.onChatCloseListeners.push(l);
};
self.onReceive = function(socket, arg) {
	self.doReceiveNotice(socket, arg);
	self.onReceiveListeners.each(function(l) {
		l(self, {socket: socket, param: arg});
	});
};

self.receiveable = true;

self.connectNotifyServer = function() {
	self._noticeSocket = dorado.Socket.connect({
		service: "noticeController#connectServer",
		parameter: "${loginUsername}",
		onReceive: function(arg) {
			self.onReceive(view._notifySocket, arg);
		},
		onDisconnect: function() {

		}
	});
	self._noticeSocket._protocol._pollingAjaxEngine.addListener("onDisconnect", function(self, arg) {
		if (arg.result._connObj.conn.responseURL.indexOf(arg.result.url) === -1) {
			window.location.href = "${configure['bdf3.loginSuccessPage']}";
		}
	});
};

self.send = function(notice) {
	self._noticeSocket.send(notice.type, notice);
}

self.connectNotifyServer();

self.doReceiveNotice = function(s, arg) {
	if(arg.type === "message") {
		var groups = dsActiveGroup.getData();
		var group = null;
		groups.each(function(g) {
			if (g.get("id") === arg.data.groupId) {
				group = g;
				return false;
			}
		});

		var callback = function() {
			if (!group.noticeCount) {
				group.noticeCount = 0;
			}

			if (groups.getFirst() !== group) {
				groups.insert(group, "begin");
				group.setState(dorado.Entity.STATE_NONE);
				group.get("functions").insert({});
			}
			arg.data.$dataType = "Notice";
			var template = self.getTemplate(arg.data, group);
			group._template = template;
			group.set("lastNotice", arg.data);
			var entity = group.createChild("notices", group.get("lastNotice").toJSON());


			if (arg.data.sender === "${loginUsername}") {
				return;
			} else {
				self.noticeCount++;
				group.noticeCount++;
			}
			if (groups.refreshCountTask) {
				clearTimeout(group.refreshCountTask);
			}

			group.refreshCountTask = setTimeout(function() {
				var count = group.noticeCount > 99 ? "99+" : group.noticeCount;
				$("#group-count-" + group.entityId).css("display", group.noticeCount ? "inline-block" : "none").text(count);
				if (group.noticeCount) {
					var count = group.noticeCount > 99 ? "99+" : group.noticeCount;
					$("#group-count-" + group.entityId).css("display", group.noticeCount ? "inline-block" : "none").text(count);
					if (group.noticeCount > 9) {
						$("#group-count-" + group.entityId).css("padding", "2px 4px");
					} else {
						$("#group-count-" + group.entityId).css("padding", "2px");
					}

				}
			}, 80);
		};
		if (group) {
			callback();
		} else {
			dsAG.set("parameter", {
				groupId: arg.data.groupId,
				memberId: "${loginUsername}"
			}).flush();
			group = groups.insert(dsAG.getData(), "begin");
			callback();
		}

	} else if (arg.type === "mark-read") {
		var groups = dsActiveGroup.getData();
		groups.each(function(g) {
			if (g.get("id") === arg.data.groupId) {
				self.noticeCount -= g.noticeCount;
				g.noticeCount = 0;
				$("#group-count-" + g.entityId).css("display", "none");
				return false;
			}
		});
	}
};


self.getTemplate = function(notice, group) {
	if (!notice.templateId) {
		return null;
	}
	var result;
	var ts = dsGlobalTemplate.getData();
	ts.each(function(t) {
		if (notice.templateId === t.get("id")) {
			result = t;
			return false;
		}
	});
	if (!result) {
		group.get("functions").each(function(t) {
			if (notice.templateId === t.get("id") && !t.get("offline")) {
				result = t;
				return false;
			}
		});
	}
	return result;
}

if (view.chatFloating === false) {
	self.set("floating", false);
} else {
	self.set("floating", true);
}
if (view.chatWidth) {
	self.set("width", view.chatWidth);
} 
if (view.chatheight) {
	self.set("height", view.chatheight);
}


self.createLink = function(template, group) {
	var content = chatTextArea.get("value");
	var Chat = self;
	var data ="";
	var script = "";
	var style = "";
	var tip = "";
	if (template) {	
		try {
			data = JSON.parse(content);
		} catch(e){}
		var html = template.get("html");
		if (html) {
			var parts = html.split("<!---->\n");
			if (parts.length === 3 && parts[2]) {
				var context = {
				}
				try {
					context.data = data;
				} 
				catch (e) {
				}
				var _temp = _.templateSettings
				_.templateSettings = {
					interpolate: /\{\{(.+?)\}\}/g,
					evaluate: /\{\{\((.+?)\)\}\}/g,
					escape: /\{\{-(.+?)\}\}/g
				};
				tip = _.template(parts[2])(context);
				_.templateSettings = _temp;
			}
		}
		var css = template.get("css");
		if (css) {
			if (parts.length === 3) {
				var parts = css.split("/****/\n");
				tip += "<style type='text/css'>" + parts[2] + "<\/style>";
			}
		}
		var javascript = template.get("javascript");
		if (javascript) {
			var parts = javascript.split("/****/\n");
			if (parts.length === 3) {
				script = parts[2];
			}
		}
	}
	var getContent = function() {
		return chatTextArea.get("value");
	}
	var message = {
		type: "message",
		templateId: template.get("id"),
		sender: "${loginUsername}",
		groupId: group.get("id"),
		all: group.get("all"),
		content: content,
		senderGroupMember: {
			groupId: group.get("id"),
			memberId: "${loginUsername}",
			nickname: "${loginUser.nickname}"
		}
	};


	var link = new dorado.widget.Control({
		style: {
			float: "right"
		},
		tip: tip ? "" : template.get("name"), 
		exClassName: "message-tool " + template.get("icon"),
		onClick: function(self, arg) {
			if (tip) {
				if (tipControl.get("actualVisible")) {
					tipControl.hide();
				} else {
					tipControl.show({
						anchorTarget: self,
						vAlign: "top",
						align: "center",
						offsetTop: -15

					});
				}

			}
			if (script) {
				eval("(function(){"+ script +"})();");
			}
			if (template.get("url")) {
				chatarea.set("visible", false);
				workarea.set("visible", true);
				linkIFrame.set("path", template.get("url"));
				linkIFrame.reload();
			}
		}
	});

	links.addChild(link);
	if (tip) {
		var tipControl = new dorado.widget.ToolTip({
			content: tip,
			anchorToTarget: true,
			arrowDirection: "bottom",
			arrowAlign: "center",
			className: "d-tip function-tip",
			shadowMode: "frame"
		});
		links.addChild(tipControl);
		var tipDom = tipControl.getDom();
	}

	var dom = link.getDom();

}

var group = dsActiveGroup.getData("#");
if (group && group.noticeCount > 0) {
	Chat.send({
		type: "mark-read",
		sender: "${loginUsername}",
		groupId: group.get("id")
	})

}

