Container.ClientEvent:
self.clickProxy = function(c, index) {
	if (self.current === c) {
		return;
	}
	var current = self.current;
	var temp = current.get("userData");
	current.set("userData", current.get("exClassName"));
	current.set("exClassName", temp);

	temp = c.get("userData");
	c.set("userData", c.get("exClassName"));
	c.set("exClassName", temp);

	self.current = c;

	cardBookChat.set("currentIndex", index);
}

