if (readOnly) {
	var label = new dorado.widget.Control({
		exClassName: "group-label group-member-read-only",
	});
	$(label.getDom()).text("禁止发言");
	label.render(name.getDom());	
}


avatar.render(arg.dom);
name.render(arg.dom);
description.render(arg.dom);

var member = dsGroup.getData("#.#members");
var groups = dsActiveGroup.getData();
var group = null;
if (member.get("memberId") === "${loginUsername}") {
	return;
}
groups.each(function(g) {
	if (g.get("other.memberId") === member.get("memberId")) {
		group = g;
		return false;
	}	
});
if (group) {
	if (groups.getFirst() !== group) {
		groups.insert(group, "begin");
		group.setState(dorado.Entity.STATE_NONE);
	}
} else {
	dsPLG.set("parameter", {
		memberId: "${loginUsername}",
		otherId: member.get("memberId")
	}).flushAsync(function(data) {
		if (data) {
			group = groups.insert(data.toJSON(), "begin");
			group.setState(dorado.Entity.STATE_NONE);
		} else {
			var group = groups.insert({
				privateLetter: true,
				creator: "${loginUsername}",
				other: {
					memberId: member.get("memberId"),
					nickname: member.get("nickname"),
					active: true
				}
			}, "begin");
			group.createChild("members", {
				memberId: "${loginUsername}",
				nickname: "${loginUser.nickname}",
				active: true
			});
			group.createChild("members", {
				memberId: member.get("memberId"),
				nickname: member.get("nickname"),
				active: true
			});
			updateActionActiveGroup.execute();

		}
	});
}

activeGroupList.set("visible", true);
userResult.set("visible", false);
chatOption.fireEvent("onClick", chatOption, {});

if ($(arg.event.target).hasClass("group-avatar") || $(arg.event.target).hasClass("group-name") || $(arg.event.target).hasClass("group-description")) {
	var gm = self.getCurrentItem();
	var g = gm.parent.parent;
	var administrator = gm.get("administrator");
	if (g.get("current.administrator") && gm.get("memberId") !== g.get("creator")) {
		adminSettings.set("visible", true);
		readOnlySettings.set("visible", true);
		if (gm.get("administrator")) {
			adminSettings.set("caption", "取消管理员身份");
		} else {
			adminSettings.set("caption", "设置为管理员身份");
		}

		if (gm.get("readOnly")) {
			readOnlySettings.set({
				caption: "允许发言",
				iconClass: "fa fa-microphone"
			});
		} else {
			readOnlySettings.set({
				caption: "禁止发言",
				iconClass: "fa fa-microphone-slash"
			});
		}
		menuGroupMember.show({
			position: {
				left: arg.event.pageX,
				top: arg.event.pageY
			}
		});
	}
}

