$(arg.dom).find(".group-name-edit").on("click", function() {
	var input = $("<input class='float-text-editor'>");
	$(arg.dom).append(input);
	var value = dsGroup.getData("#").get(self.get("property"))
	input.val(value)
	input.focus();
	input.on("blur", function() {
		var v = input.val();
		if (v && v !== value) {
			dsGroup.getData("#").set(self.get("property"), v);
		}
		input.remove();
	})
});

