	window.clearTimeout(self.queryTask);
}
self.queryTask = window.setTimeout(function(){
	var members = dsGroup.getData("#.members");
	var key = self.get("value");
	if (key) {
		members.parameter = {
			groupId: members.parent.get("id"),
			memberIdOrNickname: key
		};
	} else {
		members.parameter = {
			groupId: members.parent.get("id"),
			memberIdOrNickname: null
		};
	}
	members.flushAsync();
}, 150);

