var receiver = dsUser.getData("#");
var groups = dsActiveGroup.getData();
var group = null;
groups.each(function(g) {
	if (g.get("other.memberId") === receiver.get("username")) {
		group = g;
		return false;
	}	
});
if (group) {
	if (groups.getFirst() !== group) {
		groups.insert(group, "begin");
		group.setState(dorado.Entity.STATE_NONE);
	}
} else {
	dsPLG.set("parameter", {
		memberId: "${loginUsername}",
		otherId: receiver.get("username")
	}).flushAsync(function(data) {
		if (data) {
			var json = data.toJSON();
			json.other = {
				memberId: receiver.get("username"),
				nickname: receiver.get("nickname"),
				active: true
			};
			group = groups.insert(json, "begin");
			group.setState(dorado.Entity.STATE_NONE);
		} else {
			var group = groups.insert({
				privateLetter: true,
				memberCount: 2,
				creator: "${loginUsername}",
				other: {
					memberId: receiver.get("username"),
					nickname: receiver.get("nickname"),
					active: true
				}
			}, "begin");
			group.createChild("members", {
				memberId: "${loginUsername}",
				nickname: "${loginUser.nickname}",
				active: true
			});
			group.createChild("members", {
				memberId: receiver.get("username"),
				nickname: receiver.get("nickname"),
				active: true
			});
			updateActionActiveGroup.execute();

		}
	});
}

activeGroupList.set("visible", true);
userResult.set("visible", false);

